import React, { useRef, useEffect, useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import { Divider } from '@mui/material';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMagnifyingGlass } from '@fortawesome/free-solid-svg-icons';

const SearchBoxLayout = ({ 
    rooms, setRooms, 
    guests, setGuests, 
    inputValue, handleInputChange, 
    startDate, setStartDate, 
    endDate, setEndDate, 
    suggestions, handleSuggestionClick,
    dropdownVisible, toggleDropdown, applySelection, adults, setAdults, children, setChildren,
    handleFocus, handleBlur 
}) => {
    const [isFocused, setIsFocused] = useState(false);
    const dropdownRef = useRef(null);

    const handleClickOutside = (event) => {
        if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
            toggleDropdown(false);
            setIsFocused(false);
        }
    };

    useEffect(() => {
        document.addEventListener('mousedown', handleClickOutside);
        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, []);

    const handleFocusChange = (focusState) => {
        setIsFocused(focusState);
    };

    return (
        <div className="search-box">
            <div className="row">
                <div className="clickable-element where-element" onClick={() => { document.getElementById('input-box').focus(); handleFocusChange(true); }}>
                    <div className="label">Where</div>
                    <input
                        type="text"
                        id="input-box"
                        className="destination-input"
                        placeholder="Search destinations"
                        autoComplete="off"
                        value={inputValue}
                        onChange={handleInputChange}
                        onFocus={() => handleFocusChange(true)}
                        onBlur={() => handleFocusChange(false)}
                    />
                    {suggestions.length > 0 && (
                        <div className="suggestion-list">
                            <ul>
                                {suggestions.map((suggestion, index) => (
                                    <li key={index} onClick={() => handleSuggestionClick(suggestion.term)}>
                                        {suggestion.term}
                                    </li>
                                ))}
                            </ul>
                        </div>
                    )}
                </div>
                <Divider orientation="vertical" flexItem className="custom-divider" sx={{ height: '60px', margin: 'auto' }} />
                <div className="clickable-element check-element" onClick={() => { document.getElementById('check-in').focus(); handleFocusChange(true); }}>
                    <div className="label">Check in</div>
                    <DatePicker
                        selected={startDate}
                        onChange={date => setStartDate(date)}
                        selectsStart
                        startDate={startDate}
                        endDate={endDate}
                        placeholderText="Add dates"
                        className="date-input"
                        onFocus={() => handleFocusChange(true)}
                        onBlur={() => handleFocusChange(false)}
                        id="check-in"
                    />
                </div>
                <Divider orientation="vertical" flexItem className="custom-divider" sx={{ height: '60px', margin: 'auto' }} />
                <div className="clickable-element check-element" onClick={() => { document.getElementById('check-out').focus(); handleFocusChange(true); }}>
                    <div className="label">Check out</div>
                    <DatePicker
                        selected={endDate}
                        onChange={date => setEndDate(date)}
                        selectsEnd
                        startDate={startDate}
                        endDate={endDate}
                        minDate={startDate}
                        placeholderText="Add dates"
                        className="date-input"
                        onFocus={() => handleFocusChange(true)}
                        onBlur={() => handleFocusChange(false)}
                        id="check-out"
                    />
                </div>
                <Divider orientation="vertical" flexItem className="custom-divider" sx={{ height: '60px', margin: 'auto' }} />
                <div className="clickable-element who-element" onClick={() => { toggleDropdown(); handleFocusChange(true); }}>
                    <div className="label">Who</div>
                    <div className="travellers">
                        <span>{adults + children} Guests</span>
                    </div>
                    {dropdownVisible && (
                        <div className="dropdown-menu" ref={dropdownRef}>
                            <div className="dropdown-item">
                                <span>Rooms</span>
                                <div className="control-group">
                                    <button onClick={(e) => {e.stopPropagation(); setRooms(Math.max(1, rooms - 1));}}>-</button>
                                    <span>{rooms}</span>
                                    <button onClick={(e) => {e.stopPropagation(); setRooms(rooms + 1);}}>+</button>
                                </div>
                            </div>
                            <div className="dropdown-item">
                                <span>Adults</span>
                                <div className="control-group">
                                    <button onClick={(e) => {e.stopPropagation(); setAdults(Math.max(1, adults - 1));}}>-</button>
                                    <span>{adults}</span>
                                    <button onClick={(e) => {e.stopPropagation(); setAdults(adults + 1);}}>+</button>
                                </div>
                            </div>
                            <div className="dropdown-item">
                                <span>Children</span>
                                <div className="control-group">
                                    <button onClick={(e) => {e.stopPropagation(); setChildren(Math.max(0, children - 1));}}>-</button>
                                    <span>{children}</span>
                                    <button onClick={(e) => {e.stopPropagation(); setChildren(children + 1);}}>+</button>
                                </div>
                            </div>
                            <button className="apply-button" onClick={applySelection}>Apply</button>
                        </div>
                    )}
                </div>
                <button className="search-button">
                    {isFocused ? <><FontAwesomeIcon icon={faMagnifyingGlass} size='s' /> Search</> : <FontAwesomeIcon icon={faMagnifyingGlass} size='s' />}
                </button>
            </div>
        </div>
    );
};

export default SearchBoxLayout;
