import React from 'react';

const SearchBoxLayout = ({ 
    rooms, setRooms, 
    guests, setGuests, 
    inputValue, handleInputChange, 
    startDate, setStartDate, 
    endDate, setEndDate, 
    suggestions, handleSuggestionClick 
}) => {
    return (
        <div className="search-box">
            <div className="row">
                <div className="guests-rooms">
                    <select value={rooms} onChange={(e) => setRooms(e.target.value)}>
                        <option value="1">1 Room</option>
                        <option value="2">2 Rooms</option>
                        <option value="3">3 Rooms</option>
                        {/* Add more options as needed */}
                    </select>
                    <select value={guests} onChange={(e) => setGuests(e.target.value)}>
                        <option value="1">1 Guest per room</option>
                        <option value="2">2 Guests per room</option>
                        <option value="3">3 Guests per room</option>
                        {/* Add more options as needed */}
                    </select>
                </div>
            </div>
            <div className="row">
                <input
                    type="text"
                    id="input-box"
                    className="destination-input"
                    placeholder="Destinations"
                    autoComplete="off"
                    value={inputValue}
                    onChange={handleInputChange}
                />
                <div className="stay-period">
                    <span>Stay Period</span>
                    <span>{startDate} - {endDate}</span>
                </div>
                <button className="search-button"><i className="fa-solid fa-magnifying-glass"></i> Search</button>
            </div>
            {suggestions.length > 0 && (
                <div className="result-box" id="result-box">
                    <ul>
                        {suggestions.map((suggestion, index) => (
                            <li key={index} onClick={() => handleSuggestionClick(suggestion.term)}>
                                {suggestion.term}
                            </li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default SearchBoxLayout;
