import React, { useState, useEffect, useCallback } from 'react';
import Fuse from 'fuse.js';
import './style.css';

// Debounce function
const debounce = (func, delay) => {
    let timeoutId;
    return (...args) => {
        if (timeoutId) clearTimeout(timeoutId);
        timeoutId = setTimeout(() => {
            func(...args);
        }, delay);
    };
};

const SearchBox = () => {
    const [inputValue, setInputValue] = useState('');
    const [guests, setGuests] = useState(1);
    const [rooms, setRooms] = useState(1);
    const [startDate, setStartDate] = useState('');
    const [endDate, setEndDate] = useState('');
    const [suggestions, setSuggestions] = useState([]);
    const [locations, setLocations] = useState([]);
    const [fuse, setFuse] = useState(null);

    useEffect(() => {
        async function fetchData() {
            try {
                const response = await fetch('https://raw.githubusercontent.com/elankumuthan/ESC/destinations/destinations.json');
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();
                setLocations(data);

                const options = {
                    keys: ['term'],
                    includeScore: true,
                    threshold: 0.4,
                    location: 0,
                    distance: 100,
                    maxPatternLength: 32,
                    minMatchCharLength: 1,
                };
                setFuse(new Fuse(data, options));
            } catch (error) {
                console.error('Error fetching the locations:', error);
            }
        }

        fetchData();
    }, []);

    const filterSuggestions = (query) => {
        if (!query.trim() || !fuse) {
            return [];
        }

        const results = fuse.search(query);
        return results.map(result => result.item).slice(0, 5);
    };

    const debouncedFilterSuggestions = useCallback(debounce((query) => {
        setSuggestions(filterSuggestions(query));
    }, 500), [filterSuggestions]);

    const handleInputChange = (event) => {
        const query = event.target.value.trim();
        setInputValue(query);
        debouncedFilterSuggestions(query);
    };

    const handleSuggestionClick = (term) => {
        setInputValue(term);
        setSuggestions([]);
    };

    const handleClickOutside = (event) => {
        if (!event.target.closest('.search-box')) {
            setSuggestions([]);
        }
    };

    useEffect(() => {
        document.addEventListener('click', handleClickOutside);
        return () => {
            document.removeEventListener('click', handleClickOutside);
        };
    }, []);

    return (
        <div className="search-box">
            <div className="row">
                <div className="guests-rooms">
                    <select value={rooms} onChange={(e) => setRooms(e.target.value)}>
                        <option value="1">1 Room</option>
                        <option value="2">2 Rooms</option>
                        <option value="3">3 Rooms</option>
                        {/* Add more options as needed */}
                    </select>
                    <select value={guests} onChange={(e) => setGuests(e.target.value)}>
                        <option value="1">1 Guest per room</option>
                        <option value="2">2 Guests per room</option>
                        <option value="3">3 Guests per room</option>
                        {/* Add more options as needed */}
                    </select>
                </div>
            </div>
            <div className="row">
                <input
                    type="text"
                    id="input-box"
                    placeholder="Destinations"
                    autoComplete="off"
                    value={inputValue}
                    onChange={handleInputChange}
                />
                <div className="date-range">
                    <input
                        type="date"
                        placeholder="Start Date"
                        value={startDate}
                        onChange={(e) => setStartDate(e.target.value)}
                    />
                    <input
                        type="date"
                        placeholder="End Date"
                        value={endDate}
                        onChange={(e) => setEndDate(e.target.value)}
                    />
                </div>
                <button><i className="fa-solid fa-magnifying-glass"></i> Search</button>
            </div>
            {suggestions.length > 0 && (
                <div className="result-box" id="result-box">
                    <ul>
                        {suggestions.map((suggestion, index) => (
                            <li key={index} onClick={() => handleSuggestionClick(suggestion.term)}>
                                {suggestion.term}
                            </li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default SearchBox;
