import React, { useState, useEffect, useRef } from 'react';
import Fuse from 'fuse.js';
import './searchBox.css';

const SearchBox = () => {
  const [input, setInput] = useState('');
  const [locations, setLocations] = useState([]);
  const [fuse, setFuse] = useState(null);
  const [suggestions, setSuggestions] = useState([]);
  const inputBox = useRef(null);
  const resultBox = useRef(null);

  // Initialize Fuse instance with options
  const initializeFuse = (data) => {
    const options = {
      keys: ['term'],
      includeScore: true,
      threshold: 0.4,
      location: 0,
      distance: 100,
      maxPatternLength: 32,
      minMatchCharLength: 1,
    };
    setFuse(new Fuse(data, options));
  };

  // Fetch locations data
  const fetchData = async () => {
    try {
      const response = await fetch('https://raw.githubusercontent.com/elankumuthan/ESC/destinations/destinations.json');
      if (!response.ok) throw new Error('Network response was not ok');
      const data = await response.json();
      setLocations(data);
      initializeFuse(data);
      console.log('Locations loaded:', data);
    } catch (error) {
      console.error('Error fetching locations:', error);
    }
  };

  // Filter suggestions using Fuse.js
  const filterSuggestions = (query) => {
    if (!query.trim()) return [];
    const results = fuse.search(query);
    const filteredResults = results.map(result => result.item);
    return filteredResults.slice(0, 5);
  };

  // Handle input change with debounce
  const handleInputChange = (event) => {
    const query = event.target.value;
    setInput(query);
    const suggestions = filterSuggestions(query);
    setSuggestions(suggestions);
  };

  // Debounce function
  const debounce = (func, delay) => {
    let timer;
    return function() {
      const context = this;
      const args = arguments;
      clearTimeout(timer);
      timer = setTimeout(() => {
        func.apply(context, args);
      }, delay);
    };
  };

  const debouncedHandleInputChange = debounce(handleInputChange, 300);

  // Hide suggestions dropdown
  const hideSuggestionsDropdown = () => {
    setSuggestions([]);
  };

  // Event listener for click outside to hide dropdown
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (!resultBox.current.contains(event.target) && event.target !== inputBox.current) {
        hideSuggestionsDropdown();
      }
    };
    document.addEventListener('click', handleClickOutside);
    return () => document.removeEventListener('click', handleClickOutside);
  }, []);

  // Fetch initial locations data on component mount
  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div className="search-box">
      <input
        id="input-box"
        ref={inputBox}
        className="input-box"
        type="text"
        value={input}
        onChange={debouncedHandleInputChange}
        placeholder="Search for a location..."
      />
      {suggestions.length > 0 && (
        <div id="result-box" ref={resultBox} className="result-box">
          <ul>
            {suggestions.map((suggestion, index) => (
              <li
                key={index}
                onClick={() => {
                  setInput(suggestion.term);
                  hideSuggestionsDropdown();
                }}
              >
                {suggestion.term}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default SearchBox;
