import React, { useState } from 'react';

const SearchBoxLayout = ({ 
    rooms, setRooms, 
    guests, setGuests, 
    inputValue, handleInputChange, 
    startDate, setStartDate, 
    endDate, setEndDate, 
    suggestions, handleSuggestionClick 
}) => {
    const [dropdownVisible, setDropdownVisible] = useState(false);
    const [adults, setAdults] = useState(2);
    const [children, setChildren] = useState(0);

    const toggleDropdown = () => {
        setDropdownVisible(!dropdownVisible);
    };

    return (
        <div className="search-box">
            <div className="row">
                <div className="guests-rooms" onClick={toggleDropdown}>
                    <span>{rooms} Room | {adults + children} Guests per room</span>
                    {dropdownVisible && (
                        <div className="dropdown-menu">
                            <div className="dropdown-item">
                                <span>Rooms</span>
                                <button onClick={() => setRooms(Math.max(1, rooms - 1))}>-</button>
                                <span>{rooms}</span>
                                <button onClick={() => setRooms(rooms + 1)}>+</button>
                            </div>
                            <div className="dropdown-item">
                                <span>Adults</span>
                                <button onClick={() => setAdults(Math.max(1, adults - 1))}>-</button>
                                <span>{adults}</span>
                                <button onClick={() => setAdults(adults + 1)}>+</button>
                            </div>
                            <div className="dropdown-item">
                                <span>Children</span>
                                <button onClick={() => setChildren(Math.max(0, children - 1))}>-</button>
                                <span>{children}</span>
                                <button onClick={() => setChildren(children + 1)}>+</button>
                            </div>
                            <button className="apply-button" onClick={() => setDropdownVisible(false)}>Apply</button>
                        </div>
                    )}
                </div>
            </div>
            <div className="row">
                <input
                    type="text"
                    id="input-box"
                    className="destination-input"
                    placeholder="Destinations"
                    autoComplete="off"
                    value={inputValue}
                    onChange={handleInputChange}
                />
                <div className="stay-period">
                    <span>Stay Period</span>
                    <span>{startDate} - {endDate}</span>
                </div>
                <button className="search-button"><i className="fa-solid fa-magnifying-glass"></i> Search</button>
            </div>
            {suggestions.length > 0 && (
                <div className="result-box" id="result-box">
                    <ul>
                        {suggestions.map((suggestion, index) => (
                            <li key={index} onClick={() => handleSuggestionClick(suggestion.term)}>
                                {suggestion.term}
                            </li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default SearchBoxLayout;
