import React, { useState, useEffect } from 'react';
import Fuse from 'fuse.js';
import './SearchBox.css';

const SearchBox = () => {
    const [query, setQuery] = useState('');
    const [locations, setLocations] = useState([]);
    const [hotels, setHotels] = useState([]);
    const [suggestions, setSuggestions] = useState([]);
    const [fuseLocations, setFuseLocations] = useState(null);
    const [fuseHotels, setFuseHotels] = useState(null);

    useEffect(() => {
        fetchLocations();
    }, []);

    useEffect(() => {
        if (query.length >= 2) {
            fetchHotels(query);
        }
    }, [query]);

    const fetchLocations = async () => {
        try {
            const response = await fetch('https://cdn.jsdelivr.net/gh/elankumuthan/ESC@master/destinations.json');
            const data = await response.json();
            setLocations(data);
            setFuseLocations(new Fuse(data, { keys: ['term'], includeScore: true, threshold: 0.4 }));
        } catch (error) {
            console.error('Error fetching the locations:', error);
        }
    };

    const fetchHotels = async (hotelName) => {
        try {
            const response = await fetch(`https://hotelapi.loyalty.dev/api/hotels?name=${encodeURIComponent(hotelName)}`);
            const data = await response.json();
            setHotels(data);
            setFuseHotels(new Fuse(data, { keys: ['name'], includeScore: true, threshold: 0.4 }));
        } catch (error) {
            console.error('Error fetching hotels:', error);
        }
    };

    const filterSuggestions = (query) => {
        if (!query.trim()) return [];

        const locationResults = fuseLocations ? fuseLocations.search(query).map(result => result.item) : [];
        const hotelResults = fuseHotels ? fuseHotels.search(query).map(result => result.item) : [];

        return [...locationResults, ...hotelResults].slice(0, 5);
    };

    const handleInputChange = (event) => {
        const value = event.target.value.trim();
        setQuery(value);
        setSuggestions(filterSuggestions(value));
    };

    const handleSuggestionClick = (suggestion) => {
        setQuery(suggestion.term || suggestion.name);
        setSuggestions([]);
    };

    return (
        <div className="search-box-container">
            <input
                type="text"
                value={query}
                onChange={handleInputChange}
                placeholder="Destination/Hotel"
                className="search-input"
            />
            {suggestions.length > 0 && (
                <div className="suggestions-dropdown">
                    <ul>
                        {suggestions.map((suggestion, index) => (
                            <li key={index} onClick={() => handleSuggestionClick(suggestion)}>
                                {suggestion.term || suggestion.name}
                            </li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default SearchBox;
