import React, { useState, useEffect } from 'react';
import Fuse from 'fuse.js';
import './searchBox.css';

const SearchBox = () => {
  const [input, setInput] = useState('');
  const [locations, setLocations] = useState([]);
  const [hotels, setHotels] = useState([]);
  const [fuseLocations, setFuseLocations] = useState(null);
  const [fuseHotels, setFuseHotels] = useState(null);
  const [suggestions, setSuggestions] = useState([]);

  // Initialize Fuse instances with options
  const initializeFuse = (data, keys) => {
    const options = {
      keys,
      includeScore: true,
      threshold: 0.4,
      location: 0,
      distance: 100,
      maxPatternLength: 32,
      minMatchCharLength: 1,
    };
    return new Fuse(data, options);
  };

  // Fetch locations data
  const fetchLocations = async () => {
    try {
      const response = await fetch('https://raw.githubusercontent.com/elankumuthan/ESC/destinations/destinations.json');
      if (!response.ok) throw new Error('Network response was not ok');
      const data = await response.json();
      setLocations(data);
      setFuseLocations(initializeFuse(data, ['term']));
      console.log('Locations loaded:', data);
    } catch (error) {
      console.error('Error fetching locations:', error);
    }
  };

  // Fetch hotel data
  const fetchHotels = async (hotelName) => {
    try {
      const response = await fetch(`https://hotelapi.loyalty.dev/api/hotels?name=${encodeURIComponent(hotelName)}`);
      if (!response.ok) throw new Error(`HTTP error! Status: ${response.status}`);
      const data = await response.json();
      setHotels(data);
      setFuseHotels(initializeFuse(data, ['name']));
      console.log('Hotels loaded:', data);
    } catch (error) {
      console.error('Error fetching hotels:', error);
    }
  };

  // Filter suggestions using Fuse.js
  const filterSuggestions = (query) => {
    if (!query.trim()) return [];

    const locationResults = fuseLocations ? fuseLocations.search(query).map(result => result.item) : [];
    const hotelResults = fuseHotels ? fuseHotels.search(query).map(result => result.item) : [];

    console.log('Filtered Locations:', locationResults);
    console.log('Filtered Hotels:', hotelResults);

    return [...locationResults, ...hotelResults];
  };

  // Handle input change with debounce
  const handleInputChange = (event) => {
    const query = event.target.value;
    setInput(query);
    if (query.length >= 2) {
      fetchHotels(query);
    }
    const suggestions = filterSuggestions(query);
    setSuggestions(suggestions);
  };

  // Debounce function
  const debounce = (func, delay) => {
    let timer;
    return function (...args) {
      clearTimeout(timer);
      timer = setTimeout(() => func.apply(this, args), delay);
    };
  };

  // Hide suggestions dropdown
  const hideSuggestionsDropdown = () => {
    setSuggestions([]);
  };

  // Fetch initial locations data on component mount
  useEffect(() => {
    fetchLocations();
  }, []);

  // Event listener for click outside to hide dropdown
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (!event.target.closest('.search-box')) {
        hideSuggestionsDropdown();
      }
    };
    document.addEventListener('click', handleClickOutside);
    return () => document.removeEventListener('click', handleClickOutside);
  }, []);

  return (
    <div className="search-box">
      <input
        id="input-box"
        className="input-box"
        type="text"
        value={input}
        onChange={debounce(handleInputChange, 300)}
        placeholder="Search for a hotel..."
      />
      {suggestions.length > 0 && (
        <div id="result-box" className="result-box">
          <ul>
            {suggestions.slice(0, 5).map((suggestion, index) => (
              <li
                key={index}
                onClick={() => {
                  setInput(suggestion.term || suggestion.name);
                  hideSuggestionsDropdown();
                }}
              >
                {suggestion.term || suggestion.name}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default SearchBox;
