import React, { useState, useEffect } from 'react';
import Fuse from 'fuse.js';
import './SearchBox.css';

const SearchBox = () => {
    const [input, setInput] = useState('');
    const [hotels, setHotels] = useState([]);
    const [fuseHotels, setFuseHotels] = useState(null);
    const [suggestions, setSuggestions] = useState([]);

    useEffect(() => {
        fetchHotels('');
    }, []);

    const fetchHotels = async (hotelName) => {
        try {
            const response = await fetch(`https://raw.githubusercontent.com/elankumuthan/ESC/destinations/destinations.json`);
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }
            const data = await response.json();
            setHotels(data);
            setFuseHotels(new Fuse(data, { keys: ['name'], includeScore: true, threshold: 0.4 }));
            setSuggestions(data); // Display all suggestions initially
        } catch (error) {
            console.error('Error fetching hotels:', error);
        }
    };

    const handleInputChange = (event) => {
        const query = event.target.value;
        setInput(query);
        if (fuseHotels) {
            const results = fuseHotels.search(query);
            setSuggestions(results.map(result => result.item));
        }
    };

    return (
        <div className="search-box">
            <input
                id="input-box"
                className="input-box"
                type="text"
                value={input}
                onChange={handleInputChange}
                placeholder="Search for a hotel..."
            />
            <div id="result-box" className="result-box">
                <ul>
                    {suggestions.slice(0, 5).map((suggestion, index) => (
                        <li key={index} onClick={() => setInput(suggestion.name)}>
                            {suggestion.name}
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default SearchBox;
